pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract PaymentContract is ReentrancyGuard {
    uint256 private _paymentIdCounter;

    struct Payment {
        uint256 id;
        address payable sender;
        address payable recipient;
        uint256 amount;
        bool isPaid;
        bool isCancelled;
    }

    mapping(uint256 => Payment) public payments;
    mapping(bytes32 => uint256) public paymentLinks;

    event PaymentCreated(uint256 indexed paymentId, address indexed sender, address indexed recipient, uint256 amount);
    event PaymentPaid(uint256 indexed paymentId);
    event PaymentCancelled(uint256 indexed paymentId);

    function createPayment(address payable _recipient) public payable returns (uint256) {
        require(msg.value > 0, "Payment amount must be greater than 0");

        _paymentIdCounter++;
        uint256 newPaymentId = _paymentIdCounter;

        payments[newPaymentId] = Payment({
            id: newPaymentId,
            sender: payable(msg.sender),
            recipient: _recipient,
            amount: msg.value,
            isPaid: false,
            isCancelled: false
        });

        emit PaymentCreated(newPaymentId, msg.sender, _recipient, msg.value);
        return newPaymentId;
    }

    function generatePaymentLink(uint256 _paymentId) public returns (bytes32) {
        require(payments[_paymentId].sender == msg.sender, "Only the sender can generate a payment link");
        require(!payments[_paymentId].isPaid && !payments[_paymentId].isCancelled, "Payment must be active");

        bytes32 link = keccak256(abi.encodePacked(_paymentId, block.timestamp, msg.sender));
        paymentLinks[link] = _paymentId;

        return link;
    }

    function payWithLink(bytes32 _link) public payable nonReentrant {
        uint256 paymentId = paymentLinks[_link];
        require(paymentId != 0, "Invalid payment link");
        require(!payments[paymentId].isPaid && !payments[paymentId].isCancelled, "Payment is no longer active");
        require(msg.value == payments[paymentId].amount, "Incorrect payment amount");

        payments[paymentId].isPaid = true;
        payments[paymentId].recipient.transfer(msg.value);

        emit PaymentPaid(paymentId);
    }

    function cancelPayment(uint256 _paymentId) public nonReentrant {
        require(payments[_paymentId].sender == msg.sender, "Only the sender can cancel the payment");
        require(!payments[_paymentId].isPaid && !payments[_paymentId].isCancelled, "Payment must be active");

        payments[_paymentId].isCancelled = true;
        payable(msg.sender).transfer(payments[_paymentId].amount);

        emit PaymentCancelled(_paymentId);
    }
}

